  function addToDo() {
    var todo = document.getElementById("form").value

    if(todo !== "") {
      setTodos((currTodos) => [...currTodos, todo])
    }
    document.getElementById("form").value = ""
  }
  return (
    <AppContainer backgroundColor="white">
      <Button backgroundColor="pink">Click This Button</Button>

      {todos.map(todo => <h1>{todo.firstname}</h1>)} 
    </AppContainer>
  );
}

export default App;
